(()=>{document.querySelector("form").addEventListener("submit",(t=>{t.preventDefault()}));const t=document.getElementById("email"),e=document.getElementById("emailError");t.addEventListener("input",(n=>{t.validity.typeMismatch?e.textContent="Email must be in correct format":e.textContent=""}));const n=document.getElementById("zipcode"),o=document.getElementById("zipcodeError");n.addEventListener("input",(t=>{n.validity.patternMismatch?o.textContent="zipcode must be in 5-digit format (example: 24551) or 5-digit + 4 format (example: 24551-0001)":o.textContent=""}));const d=document.getElementById("password1"),r=document.getElementById("password2"),m=document.getElementById("password1Error"),a=document.getElementById("password2Error");let i="",s="";d.addEventListener("input",(t=>{i=d.value,d.validity.tooShort?m.textContent="Password must be at least 8 characters":m.textContent=""})),r.addEventListener("input",(t=>{s=r.value,a.textContent=i!==s?"Passwords must match":""}))})();